import { useState, useEffect } from 'react';
import Game from './Game';

function GameNews() {
  const [games, setGames] = useState([]);
  const [filteredGames, setFilteredGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGame, setSelectedGame] = useState(null);

  // Fetch all games from the Steam API once on component mount
  useEffect(() => {
    async function fetchGames() {
      try {
        const response = await fetch('https://api.steampowered.com/ISteamApps/GetAppList/v2/');
        if(!response.ok)
            throw new Error("Failed to fetch data")
        const result = await response.json();
        setGames(result.applist.apps);
      } catch (error) {
        console.error(error);
      }
    }
    fetchGames();
  }, []);

  // Filter games based on the search term
  const handleSearchChange = (event) => {
    const value = event.target.value;
    setSearchTerm(value);
    const filtered = games.filter(game =>
      game.name.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredGames(filtered);
  };

  // When a game is clicked, set it as the selected game
  const handleGameClick = (game) => {
    setSelectedGame(game);
  };

  return (
    <div>
      <h1>Game News</h1>
      <input type="text" placeholder="Search for a game" value={searchTerm} onChange={(e) => handleSearchChange(e.target.value)}/>
      <div>
        {filteredGames.slice(0, 10).map(game => (
          <p key={game.appid} onClick={() => handleGameClick(game)}>
            {game.name}
          </p>
        ))}
      </div>
      {selectedGame && <Game appid={selectedGame.appid} />}
    </div>
  );
}

export default GameNews;
