import { useEffect, useState } from 'react';

function GameDetail({ appId }){
    const [news, setNews] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {

        const fetchNews = async () => {
            setIsLoading(true);
            try {
                const response = await fetch(`http://localhost:5000/api/news?appId=${appId}`);
                if (!response.ok) 
                    throw new Error("Failed to fetch news");
        
                const result = await response.json();
                setNews(result.appnews.newsitems || []);
                setIsLoading(false);
            } catch (error) {
                console.error(error);
                setIsLoading(false);
            }
        };
        

        fetchNews();
    }, [appId]);

    return (
        <div>
            {news.length === 0 ? (
                <p>No news available for this game</p>
            ) : (
                news.map((newsItem, index) => (
                    <div   className="modal fade" 
                    id={`staticBackdrop-${index}`} 
                    data-bs-backdrop="static" 
                    data-bs-keyboard="false" 
                    tabIndex="-1" 
                    aria-labelledby={`staticBackdropLabel-${index}`} 
                    aria-hidden="true"
                    key={index}>
                        <div class="modal-dialog">
                          <div class="modal-content">
                            <div class="modal-header">
                              <h5 class="modal-title">{newsItem.title}</h5>
                              <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div class="modal-body">
                              <p>{newsItem.contents}</p>
                            </div>
                            <div class="modal-footer">
                              <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                              <button type="button" class="btn btn-primary">Save changes</button>
                            </div>
                          </div>
                        </div>
                    </div>
                ))
            )}
        </div>
    );
}

